import itertools

import rapidfuzz

from listalign.helpers import alignment_table, find_indices
from listalign.word_pyalign import align


import unittest

class AlignmentTest(unittest.TestCase):
    def contains(self, l_a, l_b, word_a, word_b, info_a = None, info_b = None, negate=False):
        a_is = find_indices(l_a, word_a)
        b_is = find_indices(l_b, word_b)
        possibilities = itertools.product(a_is, b_is)
        word_to_word_alignment, extra = align(l_a, l_b)
        print(alignment_table(word_to_word_alignment, l_a, l_b, info_b= (lambda i: info_b[i]) if info_b else None,  info_a=(lambda i: info_a[i]) if info_a else None ))
        result = any(p in word_to_word_alignment for p in possibilities)

        ata = "".join(l_a[a] for a, b in word_to_word_alignment if a is not None)
        try:
            bta = "".join(l_b[b] for a, b in word_to_word_alignment if b is not None)
        except Exception as e:
            raise e

        score = rapidfuzz.string_metric.normalized_levenshtein(ata, bta)
        ground_similarity = rapidfuzz.string_metric.normalized_levenshtein("".join(l_a), "".join(l_b))
        word_align_score = \
            sum([
                rapidfuzz.string_metric.jaro_similarity(l_a[a], l_b[b])
                for a, b in word_to_word_alignment if a
            ]) / len([x for x in word_to_word_alignment if x])
        print(f"{score=}, {ground_similarity=}, {word_align_score=}")
        assert score > 50
        if negate:
            assert (not result)

        else:
            assert(result)
    def not_contains(self, l_a, l_b, word_a, word_b, info_a = None, info_b = None, negate=False):
        self.contains(l_a, l_b, word_a, word_b, info_a = info_a, info_b = info_b, negate=True)

